import{_ as a,c as e,o as i,a4 as t}from"./chunks/framework.DpC1ZpOZ.js";const p=JSON.parse('{"title":"信息泄露","description":"","frontmatter":{},"headers":[],"relativePath":"java/Safety/10_信息泄露.md","filePath":"java/Safety/10_信息泄露.md","lastUpdated":null}'),l={name:"java/Safety/10_信息泄露.md"},r=t('<h1 id="信息泄露" tabindex="-1">信息泄露 <a class="header-anchor" href="#信息泄露" aria-label="Permalink to &quot;信息泄露&quot;">​</a></h1><h2 id="常见信息泄露问题" tabindex="-1">常见信息泄露问题 <a class="header-anchor" href="#常见信息泄露问题" aria-label="Permalink to &quot;常见信息泄露问题&quot;">​</a></h2><ul><li>泄露系统敏感信息</li><li>泄露用户敏感信息</li><li>泄露用户密码</li></ul><h2 id="信息泄露的途径" tabindex="-1">信息泄露的途径 <a class="header-anchor" href="#信息泄露的途径" aria-label="Permalink to &quot;信息泄露的途径&quot;">​</a></h2><ul><li>错误信息失控</li><li>SQL 注入</li><li>水平权限控制不当</li><li>XSS</li><li>CSRF</li></ul><h2 id="社会工程学" tabindex="-1">社会工程学 <a class="header-anchor" href="#社会工程学" aria-label="Permalink to &quot;社会工程学&quot;">​</a></h2><p>社会工程学（Social Engineering），是一种通过人际交流的方式获得信息的<strong>非技术渗透手段</strong>。</p><p>不幸的是，这种手段非常有效，而且应用效率极高。事实上，社会工程学已是企业安全最大的威胁之一。</p><div align="center"><img src="https://gitee.com/duhouan/ImagePro/raw/master/java-notes/sfatety/safety_3.png" width="425"></div><h2 id="oauth" tabindex="-1">OAuth <a class="header-anchor" href="#oauth" aria-label="Permalink to &quot;OAuth&quot;">​</a></h2><h3 id="概念" tabindex="-1">概念 <a class="header-anchor" href="#概念" aria-label="Permalink to &quot;概念&quot;">​</a></h3><p>OAuth（Open Authorization） 协议为用户资源的授权提供了一个安全的、开放而又简易的标准。与以往的授权方式不同之处是 OAuth 的授权不会使第三方触及到用户的帐号信息（如用户名与密码），即第三方无需使用用户的用户名与密码就可以申请获得该用户资源的授权，因此 OAuth 是安全的。</p><h3 id="思想" tabindex="-1">思想 <a class="header-anchor" href="#思想" aria-label="Permalink to &quot;思想&quot;">​</a></h3><ul><li>一切行为由用户授权</li><li>授权行为不泄露敏感信息</li><li>授权会过期</li></ul><h3 id="流程" tabindex="-1">流程 <a class="header-anchor" href="#流程" aria-label="Permalink to &quot;流程&quot;">​</a></h3><ol><li>登录</li><li>敏感资料模块派发票据给用户</li><li>用户带票据请求业务模块</li><li>业务使用票据请求敏感资料模块</li><li>返回票据对应数据</li><li>返回数据</li></ol><div align="center"><img src="https://gitee.com/duhouan/ImagePro/raw/master/java-notes/sfatety/safety_4.png" width="500"></div><p>特点：</p><ul><li>用户授权读取资料</li><li>无授权的资料不可读取</li><li>不允许批量获取数据</li><li>数据结构可风控审计</li></ul>',19),o=[r];function h(n,s,d,c,u,_){return i(),e("div",null,o)}const f=a(l,[["render",h]]);export{p as __pageData,f as default};
