import{_ as a,c as e,o as t,a4 as i}from"./chunks/framework.DpC1ZpOZ.js";const m=JSON.parse('{"title":"点击劫持问题","description":"","frontmatter":{},"headers":[],"relativePath":"java/Safety/5_点击劫持问题.md","filePath":"java/Safety/5_点击劫持问题.md","lastUpdated":1728037326000}'),r={name:"java/Safety/5_点击劫持问题.md"},l=i('<h1 id="点击劫持问题" tabindex="-1">点击劫持问题 <a class="header-anchor" href="#点击劫持问题" aria-label="Permalink to &quot;点击劫持问题&quot;">​</a></h1><h2 id="概念" tabindex="-1">概念 <a class="header-anchor" href="#概念" aria-label="Permalink to &quot;概念&quot;">​</a></h2><p>点击劫持（Click Jacking）是通过不可见框架底部的内容误导受害者点击，虽然受害者点击的是他所看到的网页，但其实他所点击的是被黑客精心构建的另一个置于原网页上面的透明页面。</p><h2 id="原理" tabindex="-1">原理 <a class="header-anchor" href="#原理" aria-label="Permalink to &quot;原理&quot;">​</a></h2><p>这种攻击利用了 HTML 中 <code>&lt;iframe&gt;</code>标签的透明属性。</p><p>在 HTML 中，我们可以在 iframe 里面嵌套另一个网页。通过设置 iframe 的透明度，可以使 iframe 不可见，同时在其底部放一个很有诱惑力的图片，调整网页中 iframe 中网页提交按钮的位置，使其和图片里按钮的位置相同。如此，当用户点击图片中的按钮时，其实是点击了网页中的按钮。</p><h2 id="危害" tabindex="-1">危害 <a class="header-anchor" href="#危害" aria-label="Permalink to &quot;危害&quot;">​</a></h2><ul><li>盗取用户资金（转账、消费）</li><li>获取用户敏感信息</li></ul><h2 id="防御" tabindex="-1">防御 <a class="header-anchor" href="#防御" aria-label="Permalink to &quot;防御&quot;">​</a></h2><ul><li>使用 JavaScript 禁止内嵌（JavaScript 如果被禁用，则不能 100% 解决问题）</li><li>使用 X-Frame-Options 禁止内嵌（兼容性好，推荐使用）</li><li>辅助手段：比如增加验证码验证</li></ul>',10),o=[l];function _(c,n,s,d,h,f){return t(),e("div",null,o)}const u=a(r,[["render",_]]);export{m as __pageData,u as default};
